#!/bin/bash

# Test Product Monitoring End-to-End
# This script tests the product monitoring system with real URLs

TOKEN=$(cat /tmp/auth_token.txt 2>/dev/null || echo "")
BASE_URL="http://localhost:3000"

if [ -z "$TOKEN" ]; then
    echo "❌ No auth token found. Please login first."
    exit 1
fi

echo "🔐 Using auth token: ${TOKEN:0:20}..."
echo ""

# Colors for output
GREEN='\033[0;32m'
RED='\033[0;31m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

