PLAN=$(curl -s -X GET "${BASE_URL}/me/plan" \
    -H "Authorization: Bearer ${TOKEN}" \
    -H "Content-Type: application/json" | jq -r '.plan')

echo "   User plan: ${PLAN}"
echo ""

echo "═══════════════════════════════════════════════════════════════"
echo "STEP 2: Test Product URLs"
echo "═══════════════════════════════════════════════════════════════"
echo ""

